body.script:
    function elementToButton(element) {
        if (typeof element !== 'undefined') {
            if (element != 'd') {
              	element.addClass('btn');
                element.addClass('btn-small');
                element.css('textDecoration', 'none');
            }
        }
    }
    function linkToButton(classe) {
        var reply = $(classe);
        if (reply instanceof Array) {
          for(r in reply) {
            elementToButton(r);
          }
        } else {
		  elementToButton(reply);
        }
    }

    $(document).ready(function() {
      	linkToButton('.comment .continue a');
      	linkToButton('.comment .comment-actions.secondary-text a');

		initSpoilers();
		initHljs();
		initSelectPicker();

		/* spoilerAlert */
		function initSpoilers() {
			try {
				$('spoiler, .spoiler').spoilerAlert();
			} catch (e) {
				console.log('spoiler alert is unavailable')
			}
		}

		/* hljs */
		function initHljs() {
		    if (!window.hljs) {
		      console.warn('hljs not found');
		      return;
            }
			hljs.tabReplace = '    ';
			$('pre code').each(function(i, e) {
				hljs.highlightBlock(e);
			});
		}

		function initSelectPicker() {
			var $select = $('select');
			$select.addClass('selectpicker').attr('data-live-search', true);
			$select.selectpicker();
		}
    });
